"<script>

    $(document).ready(function() {

        var date = new Date();
        var d = date.getDate();
        var m = date.getMonth();
        var y = date.getFullYear();
        var timelineInterval ; /* for timeline */
        var DateFormatString = 'yyyy-MM-dd HH:mm:ss' ;

        $('#"+ CalendarPlaceHolder.Id +"').fullCalendar({

            theme: false,
            ignoreTimezone: false,
            header: {
                left: '"+ Trim(headerLeft) +"',
                center: '"+ Trim(headerCenter) +"',
                right: '"+ Trim(headerRight) +"'
            },
            buttonText:"+ buttonText +",
            defaultView:'"+ defaultView +"',
            weekNumbers:"+ If(weekNumbers,"true","false") +",

            date:"+ Day(startDate) +",
            month:"+ (Month(startDate)-1) +",
            year:"+ Year(startDate) +",

            axisFormat: 'H:mm',

            timeFormat: 'H:mm{-H:mm }',

            titleFormat:{
                        month: 'MMMM yyyy',                     // September 2009
                        week: ""d { '&#8212;' d MMM yyyy}"",    // 7 - 13 Sep 2009
                        day: 'dddd, d MMM yyyy'                 // Tuesday, 8 Sep, 2009
                        },
            columnFormat:{
                        month: 'ddd',    // Mon
                        week: 'ddd d-M', // Mon 14-7
                        day: 'dddd d-M'  // Monday 14-7
                        },

            allDaySlot:"+ If(allDaySlots,"true","false") +",
            allDayText:'"+ allDayText +"',
            slotMinutes:"+ slotMinutes +",
            snapMinutes:"+ snapMinutes +",
            firstHour:"+ firstHour +",
            minTime:"+ minTime +",
            maxTime:"+ maxTime +",
            slotEventOverlap:"+ If(slotEventOverlap,"true","false") +",

            monthNames:["+ monthNames +"],
            monthNamesShort:["+ monthNamesShort +"],

            dayNames:["+ dayNames +"],
            dayNamesShort:["+ dayNamesShort +"],

            allDayDefault:"+ If(allDayDefault,"true","false") +",

            editable: true,
            events: "+ Events + ",

            /* ***************************************************************************************
                selectable:true will enable user to select datetime slot
                selectHelper will add helpers for selectable.
            *************************************************************************************** */
            selectable: true,
            selectHelper: true,
            select: function(start, end, allDay)
            {

                $("+e_cal.Id+").val(xDate(dFormat(start),dFormat(end),allDay));
                $("+ln_notify.Id+").click();
                "+Callback_Select+"
            },

            eventClick: function(calEvent, jsEvent, view) {
                calEvent.ignoreTimezone=false;

                $("+e_cal.Id+").val(sCal(calEvent));
                $("+ln_notify.Id+").click();

                "+Callback_Click+"
            },

            eventDrop: function(calEvent,dayDelta,minuteDelta,allDay,revertFunc) {

                $("+e_cal.Id+").val(sCal(calEvent));
                $("+ln_notify.Id+").click();

                "+CallBack_Drop+"
            },

            eventResize: function(calEvent,dayDelta,minuteDelta,revertFunc) {

                $("+e_cal.Id+").val(sCal(calEvent));
                $("+ln_notify.Id+").click();

                "+CallBack_Resize+"
            },

            eventRender: function(event, element)
            {
                element.find('.fc-event-title').after('<div class=\'fc-event-desc\'>' + event.description+'</div>');
            },

            viewDisplay: function(view) {
                window.clearInterval(timelineInterval);
                timelineInterval = window.setInterval(setTimeline, 10000);
                try {
                    setTimeline();
                } catch(err) { }
            },

            loading: function(bool) {
              if (bool)
                $('"+loadingID+"').show();
              else
                $('"+loadingID+"').hide();
            }

        });
            //DateEvent
            function xDate(sdate,edate,allDay){
                var s='DATE|';
                s=s+0;
                s=s+'|';
                //s=s+calEvent.title;
                s=s+'|';
                if (allDay)
                  s=s+allDay;
                else
                    s=s+'false';
                s=s+'|';
                s=s+sdate;
                s=s+'|';
                s=s+edate;
                s=s+'|';
                //s=s+calEvent.url;
                s=s+'|';
                s=s+'true';
                s=s+'|';
                //s=s+calEvent.color;
                s=s+'|'
                //s=s+calEvent.description;
                return s;
            }
            //calEvent
            function sCal(calEvent){
                var s='CAL|';
                if (calEvent.id)
                    s=s+calEvent.id;
                else
                     s=s+'0';
                s=s+'|';
                s=s+calEvent.title;
                s=s+'|';
                s=s+calEvent.allDay;
                s=s+'|';
                s=s+dFormat(calEvent.start);
                s=s+'|';
                s=s+dFormat(calEvent.end);
                s=s+'|';
                if (calEvent.url)
                    s=s+calEvent.url;
                else
                    s=s+'';
                s=s+'|';
                if (calEvent.editable!=null)
                    s=s+calEvent.editable;
                else
                    s=s+'true';
                s=s+'|';
                if (calEvent.color)
                    s=s+calEvent.color;
                else
                    s=s+'';
                s=s+'|';
                if (calEvent.description)
                    s=s+calEvent.description;
                else
                    s=s+'';
                return s;
            }
            // date format
            function dFormat (xDate) {
                now = xDate;
                if (!now){
                    s = '1900-01-01 00:00:00';
                    return s;
                }

                year = '' + now.getFullYear();
                month = '' + (now.getMonth() + 1); if (month.length == 1) { month = '0' + month; }
                day = '' + now.getDate(); if (day.length == 1) { day = '0' + day; }
                hour = '' + now.getHours(); if (hour.length == 1) { hour = '0' + hour; }
                minute = '' + now.getMinutes(); if (minute.length == 1) { minute = '0' + minute; }
                second = '' + now.getSeconds(); if (second.length == 1) { second = '0' + second; }
                return year + '-' + month + '-' + day + ' ' + hour + ':' + minute + ':' + second;
            }

            // Is used for Current Time START
            function setTimeline() {
                var curTime = new Date();
                if (curTime.getHours() == 0 && curTime.getMinutes() <= 5) // Because I am calling this function every 5 minutes
                {// the day has changed
                    var todayElem = $('.fc-today');
                    todayElem.removeClass('fc-today');
                    todayElem.removeClass('fc-state-highlight');

                    todayElem.next().addClass('fc-today');
                    todayElem.next().addClass('fc-state-highlight');
                }

                var parentDiv = $('.fc-agenda-slots:visible').parent();
                var timeline = parentDiv.children('.timeline');
                var timelineNowptr = parentDiv.children('.timelineNowptr');

                if (timeline.length == 0) { //if timeline isn't there, add it
                    timeline = $('<hr>').addClass('timeline');
                    parentDiv.prepend(timeline);

                    timelineNowptr = $('<span>').addClass('timelineNowptr');
                    parentDiv.prepend(timelineNowptr);
                }

                var curCalView = $('#"+ CalendarPlaceHolder.Id +"').fullCalendar('getView');
                if (curCalView.visStart < curTime && curCalView.visEnd > curTime) {
                    timeline.show();
                    timelineNowptr.show();
                } else {
                    timeline.hide();
                    timelineNowptr.hide();
                }

                if(typeof curCalView.getMinMinute !== 'undefined')
                {
                    var startSeconds = curCalView.getMinMinute()*60;
                    var endSeconds = curCalView.getMaxMinute()*60;
                    var curSeconds = (curTime.getHours() * 60 * 60) + (curTime.getMinutes() * 60) + curTime.getSeconds();
                    var percentOfDay = (curSeconds-startSeconds) / (endSeconds-startSeconds);
                    var topLoc = Math.floor(parentDiv.height() * percentOfDay);
                    timeline.css('top', topLoc + 'px');
                    timelineNowptr.css('top', (topLoc-4) + 'px');
                }

                if (curCalView.name == 'agendaWeek') { //week view, don't want the timeline to go the whole way across
                    var dayCol = $('.fc-today:visible');
                    if (dayCol.position() != null) {
                        var left = dayCol.position().left + 1;
                        var width = dayCol.width();
                        timeline.css({
                            left: left + 'px',
                            width: width + 'px'
                        });
                    }
                }
            }
            // Is used for Current Time END


    });

</script>
"

