            //DateEvent
            function xDate(sdate,edate,allDay){
                var s='DATE|';
                s=s+0;
                s=s+'|';
                //s=s+calEvent.title;
                s=s+'|';
                if (allDay)
                  s=s+allDay;
                else
                    s=s+'false';
                s=s+'|';
                s=s+sdate;
                s=s+'|';
                s=s+edate;
                s=s+'|';
                //s=s+calEvent.url;
                s=s+'|';
                s=s+'true';
                s=s+'|';
                //s=s+calEvent.color;
                s=s+'|'
                //s=s+calEvent.description;
                return s;
            }
            //calEvent
            function sCal(calEvent){
                var s='CAL|';
                if (calEvent.id)
                    s=s+calEvent.id;
                else
                     s=s+'0';
                s=s+'|';
                s=s+calEvent.title;
                s=s+'|';
                s=s+calEvent.allDay;
                s=s+'|';
                s=s+dFormat(calEvent.start);
                s=s+'|';
                s=s+dFormat(calEvent.end);
                s=s+'|';
                if (calEvent.url)
                    s=s+calEvent.url;
                else
                    s=s+'';
                s=s+'|';
                if (calEvent.editable!=null)
                    s=s+calEvent.editable;
                else
                    s=s+'true';
                s=s+'|';
                if (calEvent.color)
                    s=s+calEvent.color;
                else
                    s=s+'';
                s=s+'|';
                if (calEvent.description)
                    s=s+calEvent.description;
                else
                    s=s+'';
                return s;
            }
            // date format
            function dFormat (xDate) {
                now = xDate;
                if (!now){
                    s = '1900-01-01 00:00:00';
                    return s;
                }

                year = '' + now.getFullYear();
                month = '' + (now.getMonth() + 1); if (month.length == 1) { month = '0' + month; }
                day = '' + now.getDate(); if (day.length == 1) { day = '0' + day; }
                hour = '' + now.getHours(); if (hour.length == 1) { hour = '0' + hour; }
                minute = '' + now.getMinutes(); if (minute.length == 1) { minute = '0' + minute; }
                second = '' + now.getSeconds(); if (second.length == 1) { second = '0' + second; }
                return year + '-' + month + '-' + day + ' ' + hour + ':' + minute + ':' + second;
            }

