"<script language='javascript' type='text/javascript'>
$(document).ready(function(){
    var url = $('.MainBanner .'+$('.MainBanner:nth-child(1)').attr('backImage')+' ').attr('src');
    $('.Main_Background').css('background', ""url('"" + url + ""') no-repeat"").fadeIn(10);
    $.cookie('PN_BACKGROUND', url, { expires: 7, path: '/' });

    var bannerStatusAux = $.cookie('PN_BANNERSTATUS');
    if(bannerStatusAux != null){
        var bannerStatus = bannerStatusAux.split('.')[0];
        var bannerDate = bannerStatusAux.split('.')[1];

        if(bannerDate<'"+DateToText(GetNews.List.Current.News.NewsDate)+"') {
            if(bannerStatus == 'colapsed'){
                $.cookie('PN_BANNERSTATUS', 'expanded', { expires: 7, path: '/' });
                bannerStatus = 'expanded';
            }   
        }  
    }

    /*Tooltip*/
    var moreNewsLnk = $('#" + MoreNews.Id + "');
    if((bannerDate<'"+DateToText(GetNews.List.Current.News.NewsDate)+ "') || (bannerDate==null && "+ToLower(GetNews.List.Current.News.NewsDate>=CurrDate())+"==true)) {
            /*Mostra tooltip para quando existe uma noticia nova*/
            moreNewsLnk.tooltipster('show').tooltipster('disable');

            /*Esconde a tooltip ao fim de 5 segundos*/
            setTimeout(function() { 
                moreNewsLnk.tooltipster('hide').tooltipster('disable'); 
            },5000); 
     } else {
            /*Por omissao esconde a tooltip*/
            moreNewsLnk.tooltipster('hide').tooltipster('disable'); 
    }   

    if(bannerStatus == null || bannerStatus != 'colapsed'){
        $('.BannerExpanded').show()
        setupCarrousel('" + BannerContainer.Id + "','" + DivPagination.Id + "','" + lnkPrevious.Id + "','" + lnkNext.Id + "');
    }
    else{
        var htmlText = $('.MainBanner:nth-child(1) .MainBannerTitle').html().replace(/<br>/g,' ');
        $('.BannerColapsedText').html(htmlText);

        if($.browser.msie && $.browser.version <= 8)
            $('.Main_Background_Overlay').css('filter', 'alpha(opacity=90)');

        $('.Main_Background_Overlay').fadeIn();

        $('.BannerColapsed').show();
    }  

    $('#" + BtnColapse.Id + "').click(function(){
        var htmlText = $('.MainBanner:visible .MainBannerTitle').html().replace(/<br>/g,' ');
        $('.BannerColapsedText').html(htmlText);
        $('#" + BannerContainer.Id + "').trigger('stop', true);

        $('#" + MoreNews.Id + "').tooltipster('hide').tooltipster('disable'); 
        $.cookie('PN_BANNERSTATUS', 'colapsed.'+'"+GetNews.List.Current.News.NewsDate+"', { expires: 7, path: '/' });

        var originalHeight = $('.BannerExpanded').css('height');
        var newHeight = $('.BannerColapsed').css('height');

        $('.BannerExpanded').animate({height: newHeight}, 1000, function(){
            $('.BannerExpanded').hide();
            $('.BannerExpanded').css('height', originalHeight);

            if($.browser.msie && $.browser.version <= 8)
               $('.Main_Background_Overlay').css('filter', 'alpha(opacity=90)');

            $('.Main_Background_Overlay').fadeIn();

            $('.BannerColapsed').show();
        });
    });

    $('#" + BtnExpand.Id + "').click(function(){
        $.cookie('PN_BANNERSTATUS', 'expanded', { expires: 7, path: '/' });
        $('.BannerColapsed').hide();

        if($('#" + BannerContainer.Id + "').data('inited')){
            $('.BannerExpanded').slideDown(1000);
            $('#" + BannerContainer.Id + "').trigger('play', true);
        }
        else{
            $('.BannerExpanded').slideDown(1000, function(){
                setupCarrousel('" + BannerContainer.Id + "','" + DivPagination.Id + "','" + lnkPrevious.Id + "','" + lnkNext.Id + "');
            });
        }
        //$('#" + BannerContainer.Id + "').trigger('next', true);
        $('.Main_Background_Overlay').fadeOut();
    });
});
</script>"

