var @name@InitFunc = function(){
 YAHOO.namespace('TZOO.calendar');  

 YAHOO.TZOO.calendar.skipSelect = false;

 function handle@name@Select(){
  if(!YAHOO.TZOO.calendar.skipSelect){
   var dateInput = document.getElementById('" + InputId + "');
   var date = YAHOO.TZOO.calendar.@name@.getSelectedDates()[0];
   YAHOO.TZOO.calendar.@name@.hide();
   YAHOO.util.Event.removeListener(document,'mousedown',_onblur@name@);
   dateInput.value = (date.getMonth()+1)+'/'+date.getDate()+'/'+(date.getFullYear());
   dateInput.onchange();
  }
 }

 YAHOO.TZOO.calendar.@name@Init = function(){
  var navConfig = { 
   strings : { 
    month: 'Choose Month', 
    year: 'Enter Year', 
    submit: 'Ok', 
    cancel: 'Cancel', 
    invalidYear: 'Please enter a valid year' 
   }, 
   monthFormat: YAHOO.widget.Calendar.SHORT, 
   initialFocus: 'year' 
  };
  YAHOO.TZOO.calendar.@name@ = new YAHOO.widget.CalendarGroup( '@name@', '@name@Container',
   {PAGES:" + Pages + ", 
   pagedate:'" + InitalMonth + "', 
   mindate:'" + Month(MinDate) + "/" + Day(MinDate) + "/" + Year(MinDate) + "', 
   maxdate:'" + Month(MaxDate) + "/" + Day(MaxDate) + "/" + Year(MaxDate) + "'," + 
   If(Title <> "", "title:'" + Title + "',", "") +
   "close:false,
   iframe: true,
   width:'16em'}); 
  YAHOO.TZOO.calendar.@name@.selectEvent.subscribe(handle@name@Select, YAHOO.TZOO.calendar.@name@, true);
  YAHOO.TZOO.calendar.@name@.render(); 
 }

 function _onblur@name@(event){
  var container = document.getElementById('@name@Container');
  var elem = YAHOO.util.Event.getTarget(event);
  for (; elem != null && elem != document && elem != container; elem = elem.parentNode)
   ;
  if(elem == document){
   YAHOO.TZOO.calendar.@name@.hide();
   YAHOO.util.Event.removeListener(document,'mousedown',_onblur@name@);
  }
 }

 YAHOO.TZOO.calendar.@name@Show = function(event){
  if(!YAHOO.TZOO.calendar.@name@) YAHOO.TZOO.calendar.@name@Init();
  var d = document.getElementById('" + InputId + "').value;
  var newDate = new Date(d);

  var maxDate = new Date(YAHOO.TZOO.calendar.@name@.cfg.config.maxdate.value);
  var minDate = new Date(YAHOO.TZOO.calendar.@name@.cfg.config.mindate.value);
  if( newDate > maxDate){
   d = YAHOO.TZOO.calendar.@name@.cfg.config.maxdate.value;
   newDate = maxDate;
  }
  if( newDate < minDate ){
   d = YAHOO.TZOO.calendar.@name@.cfg.config.mindate.value;
   newDate = minDate;
  }

  YAHOO.TZOO.calendar.skipSelect = true;
  if ( d != '' && newDate.getDate() >= 0 ){
   YAHOO.TZOO.calendar.@name@.select(d);
   YAHOO.TZOO.calendar.@name@.setMonth( newDate.getMonth() );
   YAHOO.TZOO.calendar.@name@.setYear( newDate.getFullYear() );
  }

  YAHOO.TZOO.calendar.skipSelect = false;
  YAHOO.TZOO.calendar.@name@.render();
  YAHOO.TZOO.calendar.@name@.show();
  var region = YAHOO.util.Dom.getRegion('" + InputId + "');
  YAHOO.util.Dom.setXY('@name@Container', [region.left,region.bottom]);
  YAHOO.util.Event.on(document,'mousedown',_onblur@name@);
 } 

 YAHOO.util.Event.addListener('" + ImgId + "', 'click', YAHOO.TZOO.calendar.@name@Show, YAHOO.TZOO.calendar.@name@, true);
 YAHOO.util.Event.addListener('" + InputId + "', 'click', YAHOO.TZOO.calendar.@name@Show, YAHOO.TZOO.calendar.@name@, true);
}

if (document.addEventListener) {
 window.addEventListener('load', @name@InitFunc, false);
} else {
 window.attachEvent('onload', @name@InitFunc);
}

</script>", "@name@", Name)

