  for(var i = 0; i < navLinks.length; i++){
   navLinks[i].id = 'navLink' + i;
   YAHOO.util.Event.removeListener('navLink' + i, 'click');
   YAHOO.util.Event.addListener('navLink' + i, 'click', showMonthsMenu@name@, new Number(i) );
  }
 }

 function newCalNavEventsTimed() {
  setTimeout('newCalNavEvents();', 100);
 }

 function newCalNavEvents() {
  var calElem = YAHOO.TZOO.calendar.@name@;
  var leftArrow = YAHOO.util.Dom.getElementsByClassName(calElem.Style.CSS_NAV_LEFT, 'a', calElem.oDomContainer)[0];
  var rightArrow = YAHOO.util.Dom.getElementsByClassName(calElem.Style.CSS_NAV_RIGHT, 'a', calElem.oDomContainer)[0];
  leftArrow.id = 'leftArrowId';
  rightArrow.id = 'rightArrowId';
  YAHOO.util.Event.addListener('leftArrowId', 'click', newCalNavEventsTimed);
  YAHOO.util.Event.addListener('rightArrowId', 'click', newCalNavEventsTimed);
  newCalEventsTimed();
 }
 var oMenu = null;
 function showMonthsMenu@name@(event, num){
  var linkXY = YAHOO.util.Dom.getXY('navLink' + num);

  if(oMenu) {
   try {
    oMenu.clearContent();
    oMenu.destroy();
   } catch(e) {}
