  oMenu = new YAHOO.widget.Menu('navMenu', { xy:linkXY });
  oMenu.clearContent();
  setMonths(oMenu);
  oMenu.render('@name@_' + num);
  oMenu.show();
 }

 function setMonths(oMenu) {
  var monthsLabels = ['January', 'February', 'March', 'April', 'May', 'June',
     'July', 'August', 'September', 'October', 'November', 'December'];

  for(var index = 0; index < monthsLabels.length; index++) {
   oMenu.addItem({ text: monthsLabels[index], url: 'javascript:setMonth(' + index + ');' });
  }
 }

 function setMonth(num) {
  YAHOO.TZOO.calendar.@name@.setMonth(num);
  YAHOO.TZOO.calendar.@name@.render();
  newCalNavEventsTimed();
 }

 YAHOO.util.Event.addListener('" + ImgId + "', 'click', YAHOO.TZOO.calendar.@name@Show, YAHOO.TZOO.calendar.@name@, true);
 YAHOO.util.Event.addListener('" + InputId + "', 'click', YAHOO.TZOO.calendar.@name@Show, YAHOO.TZOO.calendar.@name@, true);

</script>", "@name@", Name)

