                    s=s+calEvent.editable;
                else
                    s=s+'true';
                s=s+'|';
                if (calEvent.color)
                    s=s+calEvent.color;
                else
                    s=s+'';
                s=s+'|';
                if (calEvent.description)
                    s=s+calEvent.description;
                else
                    s=s+'';
                return s;
