"<script>
    function safeJsonStringify(o){
        var cache = [];
        var blacklist = ['source','className'];
        return JSON.stringify(o, function(key, value) {
            // Exclude any attribute in the blacklist
            if($.inArray(key,blacklist) != -1) return;

            if (typeof value === 'object' && value !== null) {
                if (cache.indexOf(value) !== -1) {
                    // Circular reference found, discard key
                    return;
                }
                // Store value in collection
                cache.push(value);
            }
            return value;
        });
    }

    $(document).ready(function() {
        $('#"+ CalendarPlaceHolder.Id +"').fullCalendar({
            // General Display
            " + If(Trim(GeneralDisplayJSON) <> "", GeneralDisplayJSON + ",", "") + "

