            eventDrop: function(calEvent, delta, revertFunc, jsEvent, ui, view) {
                var calEventJS = {
                    action:""drop"",
                    range:undefined,
                    delta:delta,
                    event:calEvent
                };
                $("+e_cal.Id+").val(safeJsonStringify(calEventJS));
                $("+link_notify.Id+").click();

                "+CallBack_Drop+"
            },

            // Clicking & Hovering
            eventClick: function(calEvent, jsEvent, view) {
                var calEventJS = {
                    action:""click"",
                    range:undefined,
                    delta:undefined,
                    event:calEvent
                };
                $("+e_cal.Id+").val(safeJsonStringify(calEventJS));
                $("+link_notify.Id+").click();

                "+Callback_Click+"
            },

            // Selection
