"<script>
    function safeJsonStringify(o){
        var cache = [];
        var blacklist = ['source','className'];
        return JSON.stringify(o, function(key, value) {
            // Exclude any attribute in the blacklist
            if($.inArray(key,blacklist) != -1) return;

            if (typeof value === 'object' && value !== null) {
                if (cache.indexOf(value) !== -1) {
                    // Circular reference found, discard key
                    return;
                }
                // Store value in collection
                cache.push(value);
            }
            return value;
        });
    }

    $(document).ready(function() {
        $('#"+ CalendarPlaceHolder.Id +"').fullCalendar({
            // General Display
            " + If(Trim(GeneralDisplayJSON) <> "", GeneralDisplayJSON + ",", "") + "

            // Timezone
            " + If(Trim(TimezoneJSON) <> "", TimezoneJSON+ ",", "") + "

            // Views
            " + If(Trim(ViewsJSON) <> "", ViewsJSON + ",", "") + "

            // Agenda Options
            " + If(Trim(AgendaOptionsJSON) <> "", AgendaOptionsJSON + ",", "") + "

            // Current Date
            defaultDate:'"+ DateToText(defaultDate) +"',

            // Text/Time Customization
            " + If(Trim(TextTimeCustomizationJSON) <> "", TextTimeCustomizationJSON + ",", "") + "

            eventDrop: function(calEvent, delta, revertFunc, jsEvent, ui, view) {
                var calEventJS = {
                    action:""drop"",
                    range:undefined,
                    delta:delta,
                    event:calEvent
                };
                $('#"+e_cal.Id+"').val(safeJsonStringify(calEventJS));
                $('#"+link_notify.Id+"').click();

                "+CallBack_Drop+"
            },

            // Clicking & Hovering
            eventClick: function(calEvent, jsEvent, view) {
                var calEventJS = {
                    action:""click"",
                    range:undefined,
                    delta:undefined,
                    event:calEvent
                };
                $('#"+e_cal.Id+"').val(safeJsonStringify(calEventJS));
                $('#"+link_notify.Id+"').click();

                "+Callback_Click+"
            },

            // Selection
            " + If(Trim(SelectionJSON) <> "", SelectionJSON+ ",", "") + "
            select: function(start, end, jsEvent, view)
            {
                var calEventJS = {
                    action:""select"",
                    range:{
                        start:start,
                        end:end
                    },
                    delta:undefined,                
                    event:undefined
                };
                $('#"+e_cal.Id+"').val(safeJsonStringify(calEventJS));
                $('#"+link_notify.Id+"').click();

                "+Callback_Select+"
            },

            // Event Data
            " + If(Trim(EventDataJSON) <> "", EventDataJSON+ ",", "") + "
            eventSources: [
                " + If(EventsRESTUrl <> "", "
                {
                    url: '" + EventsRESTUrl + "',
                    cache: 'false',
                    type: 'POST'
                }", "") + "
                " + If(EventsJSON<>"", If(EventsRESTUrl <> "",", ", "") + EventsJSON, "") + "
            ],
            " + If(LoadingClass <> "", "
            loading: function(isLoading, view) {
                $('."+LoadingClass+"').toggle(isLoading);
            },", ""
            ) + "

            // Event Rendering
            eventRender: function(event, element)
            {
                element.find('.fc-title').after('<div class=\'fc-event-desc\'>' + event.description+'</div>');
            },

            // Event Dragging & Resizing
            " + If(Trim(EventDraggingAndResizingJSON) <> "", EventDraggingAndResizingJSON+ ",", "") + "
            eventResize: function(calEvent, delta, revertFunc, jsEvent, ui, view) {
                var calEventJS = {
                    action:""resize"",
                    range:undefined,
                    delta:delta,
                    event:calEvent
                };
                $('#"+e_cal.Id+"').val(safeJsonStringify(calEventJS));
                $('#"+link_notify.Id+"').click();

                "+CallBack_Resize+"
            }

        });

    });

</script>
"

