"<script type='text/javascript'>

var PPThermalSlicer = {};
PPThermalSlicer.header = "+RightTable_Header+";
PPThermalSlicer.data = "+RightTable_Data+"; // will store the data to build the table
PPThermalSlicer.footer = "+RightTable_Footer+"; // will store the data to build the table
if( typeof formatTable !== ""undefined"") 
    formatTable();

var ThermalSlicer = {};
/** Data definition **/
ThermalSlicer.Header = "+LeftTable_Header+";
ThermalSlicer.data = "+LeftTable_Data+"; // will store the data to build the table

ThermalSlicer.footer = typeof(PPThermalSlicer.footer[0])==='undefined' ? [] : " +LeftTable_Footer + "; // will store the data to build the table

ThermalSlicer.colWidths = "+LeftTable_ColWidths+";
ThermalSlicer.DataColWidth = "+RightTable_DataColWidth+";

ThermalSlicer.InEditMode = "+ToLower(InEditMode)+";

function isNormalInteger(str) {
    return /^\+?(0|[1-9]\d*)$/.test(str);
}

// allows links
function strip_tags(input, allowed) {
    // +   original by: Kevin van Zonneveld (http://kevin.vanzonneveld.net)
    allowed = (((allowed || '') + '').toLowerCase().match(/<[a-z][a-z0-9]*>/g) || []).join(''); // making sure the allowed arg is a string containing only tags in lowercase (<a><b><c>)
    var tags = /<\/?([a-z][a-z0-9]*)\b[^>]*>/gi,
        commentsAndPhpTags = /<!--[\s\S]*?-->|<\?(?:php)?[\s\S]*?\?>/gi;
    return input.replace(commentsAndPhpTags, '').replace(tags, function ($0, $1) {
      return allowed.indexOf('<' + $1.toLowerCase() + '>') > -1 ? $0 : '';
    });
}


// Setup Column with on given TD element
function applyColumnWidth(td,width) {
    $(td).css('min-width',width);
}

// Appply cell style the editable style
function editableCell(instance, td, row, col, prop, value, cellProperties) {
    Handsontable.TextCell.renderer.apply(this, arguments);
    td.className = 'changeValues';
};


// Appply cell style to  infeasible cells
function infeasibleCell(instance, td, row, col, prop, value, cellProperties) {
    Handsontable.TextCell.renderer.apply(this, arguments);
    td.className = 'noInfeasible';
    if(value === 0 || value === '0') {
            JSProdPlanSliceMaxUsable[row][col] = '';
            JSProdPlanSliceMaxUsableFiltered[row][col] = '';
    }

    applyColumnWidth(td,ThermalSlicer.DataColWidth+'px');
};


$(document).ready(function () {
    var tsstart = metricStart();
    var TableHeader = function (instance, td, row, col, prop, value, cellProperties) {
        Handsontable.TextCell.renderer.apply(this, arguments);
        td.className = 'TableHeader';
   };

    var TableDataHeader = function (instance, td, row, col, prop, value, cellProperties) {
        TableHeader(instance, td, row, col, prop, value, cellProperties);
        applyColumnWidth(td,ThermalSlicer.DataColWidth+'px');
   };

    var LeftTableHeader = function (instance, td, row, col, prop, value, cellProperties) {
        Handsontable.TextCell.renderer.apply(this, arguments);
        var escaped = Handsontable.helper.stringify(value);
        escaped = strip_tags(escaped, '<a>'); //be sure you only allow certain HTML tags to avoid XSS threats (you should also remove unwanted HTML attributes)
        td.innerHTML = escaped;
        td.className = 'TableHeader';
        $(td).width(ThermalSlicer.colWidths[col]);
        $(td).css('min-width', ThermalSlicer.colWidths[col]+'px');
   };

    var LeftTableFooter = function (instance, td, row, col, prop, value, cellProperties) {
        Handsontable.TextCell.renderer.apply(this, arguments);
        var escaped = Handsontable.helper.stringify(value);
        escaped = strip_tags(escaped, '<a>'); //be sure you only allow certain HTML tags to avoid XSS threats (you should also remove unwanted HTML attributes)
        td.innerHTML = escaped;
   };

    var LeftTable = function (instance, td, row, col, prop, value, cellProperties) {
        Handsontable.TextCell.renderer.apply(this, arguments);
        var escaped = Handsontable.helper.stringify(value);
        escaped = strip_tags(escaped, '<a>'); //be sure you only allow certain HTML tags to avoid XSS threats (you should also remove unwanted HTML attributes)
        td.innerHTML = escaped;
        $(td).width(ThermalSlicer.colWidths[col]);
        $(td).css('min-width', ThermalSlicer.colWidths[col]+'px');
   };


    // Applies the style for Alternate Colors
    var alternateColor = function (instance, td, row, col, prop, value, cellProperties) {
        Handsontable.TextCell.renderer.apply(this, arguments);
        if(td.className.indexOf('AlternateColor') == -1) //check if does not contain the class already
        {
            td.className += (checkAlternateColor(col) ? ' AlternateColor' : '' ) ;  
        }

        //replace -1 by '' because its necessary to have a value in the cells
        if(value == -1){
            value = ' '; 
        }   

        // Has Maximum slice up and down to check?
        if( PPThermalSlicer.footer.length > 0 ) {
            // Compare data table row position with the value in the Max Usable Slice Down/Up to check which slices are selected
            var rowPositionCurrent = row + 1;
            var rowPositionSliceDown = PPThermalSlicer.footer[0][col];
            var rowPositionSliceUp = PPThermalSlicer.footer[1][col];

                //is first row and Min push down or Max push up is zero, set sliceMaxZero
            if(row == 0 && (row == rowPositionSliceDown || row == rowPositionSliceUp))
                 { $(td).addClass('sliceMaxZero'); }
            if( rowPositionCurrent == rowPositionSliceDown )
                { $(td).addClass('sliceMaxDown'); }
            if( rowPositionCurrent == rowPositionSliceUp ) 
                { $(td).addClass('sliceMaxUp'); }

        }

        var sliceTable = TwoTables.getRightHandsontable();

        sliceHighlight(row, col, sliceTable, value);
        applyColumnWidth(td,ThermalSlicer.DataColWidth+'px');
    };

    /** Build Table **/
    $('#"+headertable.Id+"').handsontable(
    {
        data: PPThermalSlicer.header
      , minSpareRows: 0
      , cells: function (row, col, prop) {
                return {type: {renderer: TableDataHeader}, readOnly: true};
        }
    });

    $('#"+RightTable.Id+"').handsontable({
        data: PPThermalSlicer.data
      ,  minSpareRows: 0
      , cells: function (row, col, prop) {
            return {type: {renderer: alternateColor}, readOnly: true};
        }
    });

    $('#"+RightTableFooter.Id+"').handsontable(
    {
        data: PPThermalSlicer.footer
      , minSpareRows: 0
      , fillHandle: 'horizontal'
      , enterMoves: {row: 0, col: 0}
      , cells: function (row, col, prop) {
            if(row === os_FooterIndex.infeasible){
               return {type: {renderer: infeasibleCell}, readOnly: true};
            }
            return {type: {renderer: editableCell} };
        }
      , onBeforeChange: function (changes) {
            var i,currentCol,currentRow;
            for(i = 0;i<changes.length;i++) {
                currentCol = changes[i][1];
                currentRow = changes[i][0];

                // Parse only integers
                changes[i][3] = parseInt(changes[i][3],10);


                // Check if it's out of bounds and if is a positive number 
                if( changes[i][3] > PPThermalSlicer.data.length || ! isNormalInteger(changes[i][3]) ) {
                    changes[i][3] = changes[i][2];
                }
                // Check if current change is Max Slice Down and if is greater or equal to Max Slice Up
                else if( currentRow == 0 && changes[i][3] > PPThermalSlicer.footer[os_FooterIndex.sliceUp][currentCol]) {
                    changes[i][3] = PPThermalSlicer.footer[os_FooterIndex.sliceUp][currentCol];
                } // Check if current change is Max Slice Up and if is lower or equal to Max Slice Down
                else if (currentRow == 1 && changes[i][3] < PPThermalSlicer.footer[os_FooterIndex.sliceDown][currentCol]) {
                    changes[i][3] = PPThermalSlicer.footer[os_FooterIndex.sliceDown][currentCol];
                }
                changes[i][1] = currentCol - PlanOffset;
            }

            // if the screen is not in the edit mode notifies the changes
            if( !ThermalSlicer.InEditMode ) {
                copyToShowData();
                OsNotifyWidget(NotifyToEditMode, JSON.stringify({offset: PlanOffset, changes: changes}));
            }
        }
      , onChange: function (changes,source) {

            var $rightTable = $('#"+RightTable.Id+"');
            if(source != 'loadData') {
                for(i = 0;i<changes.length;i++) {
                    if(changes[i][3] != changes[i][2]) {
                        // Has Maximum slice up and down to check?
                        if( PPThermalSlicer.footer.length > 0 ) {

                            var $cellNew = $($rightTable.handsontable('getCell',changes[i][3]-1,changes[i][1]));
                            var $cellOld = $($rightTable.handsontable('getCell',changes[i][2]-1,changes[i][1]));

                                // for zero values it makes no sense to get changes[i][3]-1 bacuase it is zero
                            var $cellNewZero = $($rightTable.handsontable('getCell',changes[i][3],changes[i][1]));
                            var $cellOldZero = $($rightTable.handsontable('getCell',changes[i][2],changes[i][1]));


                                // Check if it was changed on Max Slice UP
                            if(changes[i][0] == os_FooterIndex.sliceUp) {

                                    // if new value is Zero set border on top
                                if(changes[i][3] == 0){
                                    $cellNewZero.addClass('sliceMaxZero');
                                }
                                else {
                                    $cellNew.addClass('sliceMaxUp');
                                    $cellOldZero.removeClass('sliceMaxZero');
                                }
                                $cellOld.removeClass('sliceMaxUp');


                                // Update values in global variable
                                JSProdPlanSliceMaxUsable[1][changes[i][1]] = changes[i][3];

                            } else {

                                if(changes[i][3] == 0){
                                   $cellNewZero.addClass('sliceMaxZero');

                                }else {
                                    $cellNew.addClass('sliceMaxDown');
                                    $cellOldZero.removeClass('sliceMaxZero');
                                }
                                $cellOld.removeClass('sliceMaxDown');

                                // Update values in global variable
                                JSProdPlanSliceMaxUsable[0][changes[i][1]] = changes[i][3];
                            }
                        }
                    }
                }

                // Recalculates Slice off limits based on Max Usable Slice Up
                RecalculateSliceOffLimits(changes);
            }
        }
    });


    $('#"+LeftTableFooter.Id+"').handsontable(
    {
        data: ThermalSlicer.footer
      , minSpareRows: 0
      , cells: function (row, col, prop) {
                return {type: {renderer: LeftTableFooter}, readOnly: true };
        }
    });

    /** Build Table **/
    $('#"+LeftTableHeader.Id+"').handsontable(
    {
        data: ThermalSlicer.Header
      , minSpareRows: 0
      , cells: function (row, col, prop) {
                return {type: {renderer: LeftTableHeader}, readOnly: true};
        }

    });

    /** Build Table **/
    $('#"+LeftTable.Id+"').handsontable(
    {
        data: ThermalSlicer.data
      , minSpareRows: 0
      , cells: function (row, col, prop) {
                return {type: {renderer: LeftTable}, readOnly: true};
        }
    });

    // tweak to fix to row selector remove TD header
    $('.handsontable thead').remove();

    // Set Heights, widths and positions
    $('#"+FooterRight.Id+"').height($('#"+FooterContainerRight.Id+"').height());
        "+
    If(Length(RightTable_Footer) > 0,
        "$('#"+Footer.Id+"').show();",
        "$('#"+Footer.Id+"').hide();$('#"+FooterLeft.Id+"').hide();")
    +"


    var tableHeight = $('#"+RightTable.Id+"').height() > $('#"+LeftTable.Id+"').height() ? $('#"+RightTable.Id+"').height() : $('#"+LeftTable.Id+"').height(); 
    var footerHeight = $('#"+FooterRight.Id+"').height() > $('#"+FooterLeft.Id+"').height() ? $('#"+FooterRight.Id+"').height() : $('#"+FooterLeft.Id+"').height(); 
    if( $('#"+DataContainer.Id+"').height() > (tableHeight + footerHeight + 4 +17) )
        $('#"+DataContainer.Id+"').height(tableHeight + footerHeight + 4 + 17);
    $('#"+Core.Id+"').width($('#"+DataContainer.Id+"').width()-$('#"+tableContainerBlocked.Id+"').width()-17-4);
    $('#"+spacerV.Id+"').height(($('#"+RightTable.Id+"').height() > $('#"+LeftTable.Id+"').height() ? $('#"+RightTable.Id+"').height() : $('#"+LeftTable.Id+"').height() ));
    $('#"+scrollV.Id+"').height($('#"+DataContainer.Id+"').height()-17-$('#"+Footer.Id+"').height()-4);

    $('#"+scrollH.Id+"').width(($('#"+RightTable.Id+"').width() > $('#"+Core.Id+"').width() ? $('#"+Core.Id+"').width() : 0));
    $('#"+scrollH.Id+"').height($('#"+DataContainer.Id+"').height() );
    $('#"+spacerH.Id+"').width(($('#"+RightTable.Id+"').width() > $('#"+headertable.Id+"').width() ? $('#"+RightTable.Id+"').width() : $('#"+headertable.Id+"').width()));
    $('#"+spacerH.Id+"').height($('#"+scrollH.Id+"').height()-17);
    $('#"+RightHeader.Id+"').width($('#"+Core.Id+"').width());
    $('#"+tableContainerBlocked.Id+"').height($('#"+scrollV.Id+"').height());
    $('#"+tableContainer.Id+"').height($('#"+scrollV.Id+"').height());
    $('#"+FooterLeft.Id+"').width($('#"+tableContainerBlocked.Id+"').width());
    $('#"+FooterRight.Id+"').width($('#"+Core.Id+"').width());
    $('#"+scrollH.Id+"').css( { left: $('#"+Core.Id+"').position().left + 'px' });
    $('#"+RightHeader.Id+"').css( { left: $('#"+Core.Id+"').position().left + 'px' });



    $('#"+scrollH.Id+"').scroll(function(e) {
        var scrollLeft = $(this).scrollLeft();
        $('#"+RightTable.Id+"').css( { left:  - scrollLeft + 'px' } );
        $('#"+headertable.Id+"').css( { left:  - scrollLeft + 'px' } );
        $('#"+FooterContainerRight.Id+"').css( { left:  - scrollLeft + 'px' } );
        lastScrollLeft = scrollLeft;
    });

    $('#"+scrollV.Id+"').scroll(function(e) {
        var scrollTop = $(this).scrollTop();
        $('#"+RightTable.Id+"').css( { top:  - scrollTop + 'px' } );
        $('#"+LeftTable.Id+"').css( { top:  - scrollTop + 'px' } );
        lastScrollTop = scrollTop;
    });

    if (typeof(lastScrollLeft) == 'undefined')
        lastScrollLeft = 0;
    if (typeof(lastScrollTop) == 'undefined')
        lastScrollTop = 0;

    if(lastScrollLeft)
        $('#"+scrollH.Id+"').scrollLeft(lastScrollLeft);
    if(lastScrollTop)
        $('#"+scrollV.Id+"').scrollTop(lastScrollTop);

    selectedOnDoubleClickCopy('"+RightTableFooter.Id+"');
    metricEnd('TSM_MultipleScrollsTable DOCUMENT READY',tsstart);
});


var TwoTables = {};

TwoTables.reloadRightTable = function (data) {
    $('#"+RightTable.Id+"').data('handsontable').loadData(data);
}

TwoTables.reloadRightTableFooter = function (data) {
    $('#"+RightTableFooter.Id+"').data('handsontable').loadData(data);
}


TwoTables.getRightHandsontable = function() { 
    return $('#"+RightTable.Id+"').data('handsontable'); 
}

TwoTables.getLeftHandsontable = function() { 
    return $('#"+LeftTable.Id+"').data('handsontable'); 
}

TwoTables.getRightFooterHandsontable = function() { 
    return $('#"+RightTableFooter.Id+"').data('handsontable'); 
}

function selectedOnDoubleClickCopy(tableId) {
    var table = $('#'+tableId).data('handsontable'); ;

    var onDoubleClick = function() {
        var selectedCell = table.getSelected();

        if(!selectedCell)
            return;

        var row = selectedCell[2];
        var column = selectedCell[3];

        if(!table.getCellMeta(row,column).isWritable)
            return;

        var value = table.getDataAtCell(row,column);
        var length = TwoTables.getRightHandsontable().getData()[row].length;

        var changes = [];
        var change = undefined;

        for(column; column < length; column++) {
            change = [row,column,value];
            changes.push(change);
        }

        table.setDataAtCell(changes,'program');
    };

    $('#' + tableId + ' .htFillHandle').dblclick(onDoubleClick);
}
</script>"

